import React, { useState } from 'react';

import Option from './Option/Option';
import Button from '../../../components/UI/Button/Button';

import classes from './FilterOptions.module.css';

import arrowLeft from '../../../assets/FilterOptions/arrow-left.svg';

const FilterOptions = () => {

    const[showFilter, setShowFilter] = useState(true);

    let options = ["Nearest my location", "Highest rating"];

    return (
        <div className={classes.FilterOptions}>
            <div className={classes.top}>
                <p>Filter</p>
            </div>
            <img src={arrowLeft} onClick={() => {
                setShowFilter(false);
            }} alt="Arrow Left"/>
            <ul className={classes.options}>        
                {options.map(option => {
                    return <Option name={option}/>
                })}
            </ul>
            <div className={classes.bottom}>
                <Button buttonSize="large">Show results</Button>
            </div>
        </div>
    );

};

export default FilterOptions;
