import React from 'react';

import classes from './Search.module.css';

import searchIcon from '../../../assets/HeroSection/Input/search.png';
import locationIcon from '../../../assets/HeroSection/Input/location.png';

const Search = (props) => {

    let search;

    let attachedClasses = [classes.Search];

    switch (props.forComponent) {
        case "Hero":
            search = <form className={attachedClasses.join(' ')}>
                        <div className={classes.part}>
                            <img src={searchIcon} alt="Search Icon"/>
                            <input type="text" placeholder="Write service, performer or company"/>
                        </div>    
                        <div className={classes.part}>
                            <img src={locationIcon} alt="Location Icon"/>
                            <input type="text" placeholder="Write city or area"/>
                        </div>
                        <button type="submit" value="submit">Search</button>
                    </form>;
            break;
        case "Header":
            attachedClasses.push(classes.border);
            search = <form className={attachedClasses.join(' ')}>
                        <div className={classes.part}>
                            <img src={searchIcon} alt="Search Icon"/>
                            <input type="text" placeholder="Write service, performer or company"/>
                        </div>    
                        <div className={classes.part}>
                            <img src={locationIcon} alt="Location Icon"/>
                            <input type="text" placeholder="Write city or area"/>
                        </div>
                        <button type="submit" value="submit">Search</button>
                    </form>;
            break;
        default:
            search = <form className={attachedClasses.join(' ')}>
                        <div className={classes.part}>
                            <img src={searchIcon} alt="Search Icon"/>
                            <input type="text" placeholder="Write service, performer or company"/>
                        </div>    
                        <div className={classes.part}>
                            <img src={locationIcon} alt="Location Icon"/>
                            <input type="text" placeholder="Write city or area"/>
                        </div>
                        <button type="submit" value="submit">Search</button>
                    </form>;
            break;      
    }

    return search;

}

export default Search;
